import React, { useState, ReactNode } from 'react';
import { useWallet, useConnection } from '@solana/wallet-adapter-react';
import { WalletMultiButton } from '@solana/wallet-adapter-react-ui';
import { PublicKey, Keypair } from '@solana/web3.js';
import { getAssociatedTokenAddress } from '@solana/spl-token';
import { AiNftBrowserClient } from '../utils/ai-nft-program';
import './NFTGenerator.css';

interface NFTGeneratorProps {
  onGenerate?: (imageUrl: string) => void;
}

const NFTGenerator: React.FC<NFTGeneratorProps> = ({ onGenerate }) => {
  const { publicKey, sendTransaction } = useWallet();
  const { connection } = useConnection();
  const [prompt, setPrompt] = useState('');
  const [size, setSize] = useState<'small' | 'medium' | 'large'>('medium');
  const [share, setShare] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isMinting, setIsMinting] = useState(false);
  const [generatedImage, setGeneratedImage] = useState<string | null>(null);
  const [shareUrl, setShareUrl] = useState<string | null>(null);
  const [error, setError] = useState<ReactNode | null>(null);
  const [client] = useState(() => new AiNftBrowserClient(connection));

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!publicKey) {
      setError('Please connect your wallet first');
      return;
    }
    if (!prompt) {
      setError('Please enter a prompt');
      return;
    }

    setIsGenerating(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:3001/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt,
          walletAddress: publicKey.toString(),
          size,
          share
        }),
      });

      const data = await response.json();
      if (!data.success) {
        throw new Error(data.error || 'Failed to generate NFT');
      }

      setGeneratedImage(data.imageUri);
      setShareUrl(data.shareUrl);
      if (onGenerate) {
        onGenerate(data.imageUri);
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to generate NFT';
      setError(errorMessage);
    } finally {
      setIsGenerating(false);
    }
  };

  const handleShare = () => {
    if (shareUrl) {
      const shareData = {
        title: 'Check out my AI-generated NFT!',
        text: `Generated using AI NFT Creator: ${prompt}`,
        url: shareUrl
      };

      if (navigator.share && /mobile|android|iphone/i.test(navigator.userAgent)) {
        // Use native share on mobile devices
        navigator.share(shareData).catch(console.error);
      } else {
        // Fallback to clipboard copy on desktop
        navigator.clipboard.writeText(shareUrl);
        alert('Share URL copied to clipboard! You can now paste it anywhere.');
      }
    }
  };

  const handleDownload = () => {
    if (generatedImage) {
      const link = document.createElement('a');
      link.href = generatedImage;
      link.download = `AI-NFT-${Date.now()}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  const handleMint = async () => {
    if (!generatedImage || !publicKey || !client) return;
    
    setIsMinting(true);
    setError(null);
    
    try {
      // Generate a new mint keypair
      const mintKeypair = Keypair.generate();
      const mintPubkey = mintKeypair.publicKey;
      
      // Get token account
      const tokenAddress = await getAssociatedTokenAddress(
        mintPubkey,
        publicKey
      );
      
      // Get metadata address
      const [metadataAddress] = await PublicKey.findProgramAddress(
        [Buffer.from('metadata'), mintPubkey.toBuffer()],
        new PublicKey(process.env.REACT_APP_PROGRAM_ID || 'YOUR_PROGRAM_ID_HERE')
      );
      
      // Create metadata
      const metadata = {
        name: `AI NFT: ${prompt.substring(0, 30)}...`,
        symbol: 'AINFT',
        description: prompt,
        image: generatedImage,
        attributes: [
          {
            trait_type: 'Generator',
            value: 'Stability AI'
          },
          {
            trait_type: 'Size',
            value: size
          },
          {
            trait_type: 'Creator',
            value: publicKey.toString()
          }
        ]
      };
      
      // Initialize NFT
      const initTx = await client.createInitializeTransaction(
        publicKey,
        mintPubkey,
        tokenAddress,
        metadataAddress,
        metadata.name,
        metadata.symbol,
        JSON.stringify(metadata)
      );
      
      const signature = await sendTransaction(initTx, connection, {
        signers: [mintKeypair]
      });
      
      await connection.confirmTransaction(signature, 'confirmed');
      
      // Mint NFT
      const mintTx = await client.createMintTransaction(
        publicKey,
        mintPubkey,
        tokenAddress,
        metadataAddress,
        Date.now().toString(),
        generatedImage,
        JSON.stringify(metadata)
      );
      
      const mintSignature = await sendTransaction(mintTx, connection);
      await connection.confirmTransaction(mintSignature, 'confirmed');
      
      alert('NFT minted successfully! Transaction signature: ' + mintSignature);
    } catch (err) {
      console.error('Error minting NFT:', err);
      setError(err instanceof Error ? err.message : 'Failed to mint NFT');
    } finally {
      setIsMinting(false);
    }
  };

  return (
    <div className="nft-generator">
      <div className="wallet-button">
        <WalletMultiButton />
      </div>
      
      <form onSubmit={handleSubmit} className="generator-form">
        <div className="input-group">
          <input
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your image prompt..."
            disabled={isGenerating}
            className="prompt-input"
          />
        </div>

        <div className="options-group">
          <div className="size-selector">
            <label>Size:</label>
            <select value={size} onChange={(e) => setSize(e.target.value as 'small' | 'medium' | 'large')}>
              <option value="small">Small (512x512)</option>
              <option value="medium">Medium (768x768)</option>
              <option value="large">Large (1024x1024)</option>
            </select>
          </div>
          
          <div className="share-option">
            <label>
              <input
                type="checkbox"
                checked={share}
                onChange={(e) => setShare(e.target.checked)}
              />
              Enable sharing
            </label>
          </div>
        </div>

        <button type="submit" disabled={isGenerating || !publicKey} className="generate-button">
          {isGenerating ? 'Generating...' : 'Generate NFT'}
        </button>
        
        {error && <div className="error-message">{error}</div>}
        
        {generatedImage && (
          <div className="generated-image">
            <img src={generatedImage} alt="Generated NFT" />
            <div className="action-buttons">
              {shareUrl && (
                <button onClick={handleShare} className="share-button">
                  <span role="img" aria-label="share">üîó</span> Share
                </button>
              )}
              <button onClick={handleDownload} className="download-button">
                <span role="img" aria-label="download">‚¨áÔ∏è</span> Download
              </button>
              <button 
                onClick={handleMint} 
                disabled={isMinting} 
                className="mint-button"
              >
                <span role="img" aria-label="mint">‚ö°</span>
                {isMinting ? 'Minting...' : 'Mint as NFT'}
              </button>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default NFTGenerator;
