import React, { useState, ReactNode } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';
import { WalletMultiButton } from '@solana/wallet-adapter-react-ui';
import './NFTGenerator.css';

interface NFTGeneratorProps {
  onGenerate?: (imageUrl: string) => void;
}

const NFTGenerator: React.FC<NFTGeneratorProps> = ({ onGenerate }) => {
  const [prompt, setPrompt] = useState('');
  const [size, setSize] = useState<'small' | 'medium' | 'large'>('medium');
  const [share, setShare] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedImage, setGeneratedImage] = useState<string | null>(null);
  const [shareUrl, setShareUrl] = useState<string | null>(null);
  const [error, setError] = useState<ReactNode | null>(null);
  const { publicKey } = useWallet();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!publicKey) {
      setError('Please connect your wallet first');
      return;
    }
    if (!prompt) {
      setError('Please enter a prompt');
      return;
    }

    setIsGenerating(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:3001/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt,
          walletAddress: publicKey.toString(),
          size,
          share
        }),
      });

      const data = await response.json();
      if (!data.success) {
        throw new Error(data.error || 'Failed to generate NFT');
      }

      setGeneratedImage(data.imageUri);
      setShareUrl(data.shareUrl);
      if (onGenerate) {
        onGenerate(data.imageUri);
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to generate NFT';
      setError(errorMessage);
    } finally {
      setIsGenerating(false);
    }
  };

  const handleShare = () => {
    if (shareUrl) {
      const shareData = {
        title: 'Check out my AI-generated NFT!',
        text: `Generated using AI NFT Creator: ${prompt}`,
        url: shareUrl
      };

      if (navigator.share && /mobile|android|iphone/i.test(navigator.userAgent)) {
        // Use native share on mobile devices
        navigator.share(shareData).catch(console.error);
      } else {
        // Fallback to clipboard copy on desktop
        navigator.clipboard.writeText(shareUrl);
        alert('Share URL copied to clipboard! You can now paste it anywhere.');
      }
    }
  };

  const handleDownload = () => {
    if (generatedImage) {
      const link = document.createElement('a');
      link.href = generatedImage;
      link.download = `AI-NFT-${Date.now()}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div className="nft-generator">
      <div className="wallet-button">
        <WalletMultiButton />
      </div>
      
      <form onSubmit={handleSubmit} className="generator-form">
        <div className="input-group">
          <input
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your image prompt..."
            disabled={isGenerating}
            className="prompt-input"
          />
        </div>

        <div className="options-group">
          <div className="size-selector">
            <label>Size:</label>
            <select value={size} onChange={(e) => setSize(e.target.value as 'small' | 'medium' | 'large')}>
              <option value="small">Small (512x512)</option>
              <option value="medium">Medium (768x768)</option>
              <option value="large">Large (1024x1024)</option>
            </select>
          </div>
          
          <div className="share-option">
            <label>
              <input
                type="checkbox"
                checked={share}
                onChange={(e) => setShare(e.target.checked)}
              />
              Enable sharing
            </label>
          </div>
        </div>

        <button type="submit" disabled={isGenerating || !publicKey} className="generate-button">
          {isGenerating ? 'Generating...' : 'Generate NFT'}
        </button>
        
        {error && <div className="error-message">{error}</div>}
        
        {generatedImage && (
          <div className="generated-image">
            <img src={generatedImage} alt="Generated NFT" />
            <div className="share-buttons">
              {shareUrl && (
                <button onClick={handleShare} className="share-button">
                  <span role="img" aria-label="share">üîó</span> Share NFT
                </button>
              )}
              <button onClick={handleDownload} className="download-button">
                <span role="img" aria-label="download">‚¨áÔ∏è</span> Download
              </button>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default NFTGenerator;
